import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { fetchProjectById } from '../../services/api';

function ProjectDetail() {
    const { id } = useParams();  // Получаем id проекта из URL
    const [project, setProject] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        // Запрос для получения данных о проекте по id
        fetchProjectById(id)
            .then((data) => {
                setProject(data);
                setLoading(false);
            })
            .catch((error) => {
                console.error("Ошибка при получении данных проекта:", error);
                setError(error);
                setLoading(false);
            });
    }, [id]);

    if (loading) {
        return <div>Загрузка...</div>;
    }

    if (error) {
        return <div>Произошла ошибка при загрузке данных проекта.</div>;
    }

    return (
        <section className="project-detail">
            <h2>{project.name}</h2>
            <p>{project.description}</p>
            <a href={project.link} target="_blank" rel="noopener noreferrer">
                Перейти по ссылке
            </a>
            {/* Добавь дополнительные поля, если нужно */}
        </section>
    );
}

export default ProjectDetail;
