import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchProjectById } from '../../services/api';
import './style.css'


function DetailSection(){

    const { id } = useParams(); // Получаем id из URL
    const [project, setProject] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchProjectById(id)
            .then((data) => {
                setProject(data);
                setLoading(false);
            })
            .catch((error) => {
                setError(error);
                setLoading(false);
            });
    }, [id]); // id - зависимость, чтобы запрос выполнялся при изменении id

    if (loading) {
        return <div>Загрузка...</div>;
    }

    if (error) {
        return <div>Произошла ошибка при загрузке данных проекта.</div>;
    }

    if (!project) {
        return <div>Проект не найден.</div>;
    }

    return(

        // <section className='detail-section'>

        //     <div className='detail-conteiner'>

        //         <div className='detail-image'>
        //             {/* <img src={project.image}   /> */}
        //             <img src={`http://localhost:8001${project.image}`} />
        //         </div>

        //         <div className='detail-description'>

        //             <div className='detail-items'>
        //                 <h1>{project.name}</h1>
        //             </div>

        //             <div className='detail-items-description'>
        //                 <h2>{project.description}</h2>
        //             </div>

        //             <div className='detail-items-links'>
        //             <a href={project.link}><p> Посмотреть проект в сети:<strong>{project.link}</strong></p></a>
        //             </div>

        //         </div>


        //      </div>


        // </section>

        <section class="grid-section">
        <div class="grid-container">
            <div class="image-container">
            <img src={`http://localhost:8001${project.image}`} />
            </div>
            <div class="text-container">
                <h1>{project.name}</h1>
                <h2>{project.description}</h2>
                <p>
                    Это пример текста. Вы можете добавить сюда любое описание или контент, который будет отображаться рядом с изображением.
                </p>
            </div>
        </div>
</section>


    );
}
export default DetailSection;
